"206A":{"department":"CLASSICS","number":"206A","title":"The Semantics of Grammar","description":"(Formerly CLASSGEN 205A.) Supplements CLASSICS 104A/204A. 206A: Tense, Aspect, Argument Structure, Location. 206B: Quantification, Plurality, Modification, Negation, Modality.","min_units":2,"max_units":2,"gers":[],"id":112811,"primary":[[{"primary-instructors":[],"secondary-instructors":["Devine, A."],"meeting-times":[{"days":[false,false,false,false,true],"start":"10:00:00 AM","end":"11:50:00 AM","instructors":["Devine, A."],"location":"20-21B"}],"id":28865}],[],[]],"primary_type":"Lecture"},
