"320A":{"department":"MUSIC","number":"320A","title":"Introduction to Audio Signal Processing Part I: Spectrum Analysis","description":"Digital signal representations and transforms for music and audio research. Topics: complex numbers, sinusoids, spectrum representation, sampling and aliasing,  the Discrete Fourier Transform (DFT), Fourier theorems, z transform, Laplace transform, and associated Matlab software. See http://ccrma.stanford.edu/courses/320/.","min_units":3,"max_units":4,"gers":[],"id":122012,"primary":[[{"primary-instructors":[],"secondary-instructors":["Smith, J."],"meeting-times":[{"days":[false,true,false,true,false],"start":"3:15:00 PM","end":"5:05:00 PM","instructors":["Smith, J."],"location":"Knoll217"}],"id":18602}],[],[]],"primary_type":"Lecture","secondary":[[{"primary-instructors":[],"secondary-instructors":["Gnegy, C."],"meeting-times":[{"days":[false,true,false,true,false],"start":"3:15:00 PM","end":"5:05:00 PM","instructors":["Gnegy, C."],"location":""}],"id":18771}],[],[]],"secondary_type":"Discussion"},
