"50":{"department":"PHYSICS","number":"50","title":"Astronomy Laboratory and Observational Astronomy","description":"Introduction to observational astronomy emphasizing the use of optical telescopes. Observations of stars, nebulae, and galaxies in laboratory sessions with 16- and 24-inch telescopes at the Stanford Observatory. Meets one evening per week from dusk until well after dark at the Stanford Observatory. No previous physics required. Limited enrollment. Lab.","min_units":3,"max_units":3,"gers":[" DB-NatSci","WAY-AQR","WAY-SMA"],"id":122627,"primary":[[{"instructors":[],"meeting-times":[{"days":[true,false,true,false,false],"start":"2:15:00 PM","end":"3:05:00 PM","location":"Hewlett Teaching Center Rm 101"}],"id":19380}],[],[]],"primary_type":"Lecture","secondary":[[{"instructors":[],"meeting-times":[{"days":[false,false,false,false,false],"start":"7:30:00 PM","end":"11:30:00 PM","location":""}],"id":23895},{"instructors":[],"meeting-times":[{"days":[false,false,false,false,false],"start":"7:30:00 PM","end":"11:30:00 PM","location":""}],"id":23896}],[],[]],"secondary_type":"Discussion"},
